INSERT INTO "GGFLFW_WX"."T_GZ_SX" VALUES ('878', '20180622122310068035', '010499', '其他现场监督', NULL, '00050002', '微信版', '1', NULL, '12420600MB1002470J', '0', '系统维护', TO_DATE('2018-06-22 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '襄阳公证处管理', TO_DATE('2018-06-26 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '1', '现场监督公证是公证业务的一个重要组成部分，具体指公证机构对公司股东大会或发起人会议、创立大会以及招标投标、拍卖、抽签、开奖、评选、商品抽样、检测、投票选举等活动的程序进行现场监督，并对活动结果给予确认，公证的活动。', '无', '申办', NULL, NULL);
INSERT INTO "GGFLFW_WX"."T_GZ_SX" VALUES ('886', '20180622140008397059', '011403', '户籍注销', NULL, '00050002', '微信版', '1', NULL, '12420600MB1002470J', '0', '襄阳公证处管理', TO_DATE('2018-06-22 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '襄阳公证处管理', TO_DATE('2018-06-26 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '1', '户籍注销公证，是公证机构根据当事人的申请，依法证明当事人因定居国外或港澳台地区而注销其户籍事实的真实性和合法性的活动。', '1、自然人申请人的身份证明（如身份证、护照等）、户口簿原件及其A4复印件一份，已离境的应提供护照复印件；
