INSERT INTO "GGFLFW_WX"."T_GZ_SX" VALUES ('834', '20180622114713473013', '01150137', '居民户口簿', NULL, '00050002', '微信版', '1', NULL, '12420600MB1002470J', '0', '系统维护', TO_DATE('2018-06-22 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '襄阳公证处管理', TO_DATE('2018-06-26 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '1', '居民身份证、户口簿和护照是当事人身份的依据。身份证明（身份证、户口簿、护照）公证是指公证机构根据当事人的申请，依法证明公证当事人持有的身份证、户口簿、护照的真实性的活动。其中，根据司法部规定，身份证公证应当指明公证书用途。', '（1）身份证；

