INSERT INTO "GGFLFW_WX"."T_GZ_SX" VALUES ('1642', '20180815101012794465', '01060101', '公司章程', NULL, '00040002', '微信版', '1', NULL, '32340000MD5015153K', '0', '庐州管理', TO_DATE('2018-08-15 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '庐州管理', TO_DATE('2018-08-15 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '0', '指公证机构根据当事人的申请，依法对当事人持有的公司章程的真实性、合法性予以证明的活动。', '（1）公司营业执照；

