INSERT INTO "GGFLFW_WX"."T_GZ_SX" VALUES ('3182', '20181216140142024499', '011101', '出生', NULL, '00050002', '微信版', '1', NULL, '01089521-5', '0', '系统维护', TO_DATE('2018-12-16 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '系统维护', TO_DATE('2018-12-16 00:00:00', 'SYYYY-MM-DD HH24:MI:SS'), '0', NULL, '1、公证申请人及其父母的身份证件
国内当事人提供：居民身份证、户口本；港、澳、台身份证件、通行证
