INSERT INTO "GGFLFW_WX"."T_GZ_SX" VALUES ('2238', '20181025154407468334', '011101', '出生', NULL, '00050002', '微信版', '1', NULL, 'jd', NULL, '钱塘管理', TO_DATE('2018-10-25 15:44:04', 'SYYYY-MM-DD HH24:MI:SS'), '钱塘管理', TO_DATE('2018-10-25 15:44:04', 'SYYYY-MM-DD HH24:MI:SS'), '0', '是指公证机构根据当事人的申请，对自然人出生时间、地点、生父母的情况予以证明的活动。根据属地原则，我国公证机构一般只办理在我国出生的自然人的出生公证。', '1.申请人的有效身份证件。
