INSERT INTO "GGFLFW_WX"."T_GZ_SX" VALUES ('2245', '20181025154433183341', '010903', '已婚(初婚)', NULL, '00050002', '微信版', '1', NULL, 'jd', NULL, '钱塘管理', TO_DATE('2018-10-25 15:44:30', 'SYYYY-MM-DD HH24:MI:SS'), '钱塘管理', TO_DATE('2018-10-25 15:44:30', 'SYYYY-MM-DD HH24:MI:SS'), '1', '公证机关根据当事人的申请，依法对其结婚状况法律事实的真实性、合法性予以证明的活动。', '1.申请人的有效身份证件。
